import {Component} from "react";

export default class Form extends Component{
    handleVlue = (event) =>{
        this.setState({
            firstname: event.target.value,
        })
    }

    render(){
        return (
            <div>
                Form
                <form>
                    <input
                        onChange = {this.handleValue}
                        type = "text"
                        value = "firstname"
                    ></input>
                </form>
            </div>
        )
    }
}
